// Read file content from BINARY data (Read/Write Files returns binary)
let fileContent = '';

console.log('=== Parse YAML Task Debug ===');
console.log('Has binary:', !!$input.item.binary);
console.log('Binary keys:', $input.item.binary ? Object.keys($input.item.binary) : 'none');

// Read from binary.data (Read/Write Files stores file content here)
if ($input.item.binary && $input.item.binary.data) {
  const buffer = Buffer.from($input.item.binary.data.data, 'base64');
  fileContent = buffer.toString('utf8');
  console.log('File read from binary.data, length:', fileContent.length);
} else {
  throw new Error('No binary data found. Check if Read/Write Files node executed correctly.');
}

console.log('First 200 chars:', fileContent.substring(0, 200));

// Parse YAML
const lines = fileContent.split('\n');
const task = {
  type: '',
  project: '',
  description: '',
  priority: 'P2'
};

let currentSection = '';
let descriptionLines = [];

for (const line of lines) {
  const trimmed = line.trim();

  if (trimmed.startsWith('type:')) {
    task.type = trimmed.replace('type:', '').trim();
  } else if (trimmed.startsWith('project:')) {
    task.project = trimmed.replace('project:', '').trim();
  } else if (trimmed.startsWith('description:')) {
    currentSection = 'description';
    const desc = trimmed.replace('description:', '').trim();
    if (desc && desc !== '|') {
      descriptionLines.push(desc);
    }
  } else if (currentSection === 'description' && trimmed && !trimmed.startsWith('context:') && !trimmed.startsWith('requirements:')) {
    descriptionLines.push(trimmed);
  } else if (trimmed.startsWith('context:') || trimmed.startsWith('requirements:')) {
    currentSection = '';
  }
}

// Join description
task.description = descriptionLines.join(' ').trim();

// Build full task string for context
const taskString = `${task.type}: ${task.description}`;

console.log('Parsed successfully:');
console.log('  Type:', task.type);
console.log('  Project:', task.project);
console.log('  Description length:', task.description.length);
console.log('  Task string:', taskString);

return {
  json: {
    task: taskString,
    project: task.project,
    priority: task.priority,
    type: task.type,
    description: task.description
  }
};

// Parse Claude response and extract file operations
const response = $input.item.json;

// Extract content text
let contentText = '';
if (response.content && Array.isArray(response.content)) {
  for (const block of response.content) {
    if (block.type === 'text') {
      contentText += block.text;
    }
  }
}

// Extract token usage
const usage = response.usage || {};
const tokenUsage = {
  input_tokens: usage.input_tokens || 0,
  output_tokens: usage.output_tokens || 0,
  total_tokens: (usage.input_tokens || 0) + (usage.output_tokens || 0)
};

// Calculate cost (Claude Sonnet 4.5 pricing)
const inputCost = (tokenUsage.input_tokens / 1000000) * 3.0;
const outputCost = (tokenUsage.output_tokens / 1000000) * 15.0;
const totalCost = inputCost + outputCost;

// Extract file operations from XML
console.log('DEBUG: contentText length:', contentText.length);
console.log('DEBUG: contentText preview:', contentText.substring(0, 500));

const fileOpsRegex = /<file_operations>([\s\S]*?)<\/file_operations>/gi;
const operations = [];

let match;
while ((match = fileOpsRegex.exec(contentText)) !== null) {
  const xmlContent = match[1];

  // Parse individual operations
  const opRegex = /<operation\s+type="([^"]+)"\s+path="([^"]+)">([\s\S]*?)<\/operation>/gi;

  let opMatch;
  while ((opMatch = opRegex.exec(xmlContent)) !== null) {
    const type = opMatch[1];
    const path = opMatch[2];
    const fullContent = opMatch[3];

    // Extract content between <content> tags
    const contentMatch = fullContent.match(/<content>([\s\S]*?)<\/content>/i);
    const content = contentMatch ? contentMatch[1].trim() : '';

    if (type && path) {
      operations.push({
        type: type,
        path: path,
        content: content
      });
    }
  }
}

console.log(`Found ${operations.length} file operations`);

return {
  json: {
    claude_response: contentText,
    token_usage: tokenUsage,
    cost_usd: totalCost,
    cost_eur: totalCost * 1.10,
    operations: operations,
    operations_count: operations.length,
    model: response.model || 'claude-sonnet-4-5-20250929'
  }
};

// Generate clean response.md
const data = $node['Parse File Operations'].json;
const execResults = $node['Execute Operations'].json;
const yamlData = $node['Parse YAML Task'].json;
const now = new Date().toISOString().replace('T', ' ').substr(0, 19);

let markdown = `# Task Response\n\n`;
markdown += `**Generated:** ${now}\n\n`;

// Task Summary
markdown += `## üìã Task Summary\n\n`;
markdown += `**Project:** ${yamlData.project}\n\n`;
markdown += `**Task:** ${yamlData.task}\n\n`;
markdown += `**Priority:** ${yamlData.priority}\n\n`;

// File Operations Summary
if (data.operations_count > 0) {
  markdown += `## üìù File Operations\n\n`;
  markdown += `**Total operations:** ${data.operations_count}\n`;

  if (execResults && execResults.file_results) {
    const successCount = execResults.file_results.filter(r => r.success).length;
    const failCount = execResults.file_results.filter(r => !r.success).length;

    markdown += `**Success:** ${successCount}\n`;
    markdown += `**Failed:** ${failCount}\n\n`;

    markdown += `**Created/Modified files:**\n`;
    for (const result of execResults.file_results) {
      const status = result.success ? '‚úÖ' : '‚ùå';
      markdown += `- ${status} **${result.operation.toUpperCase()}**: \`${result.path}\`\n`;
      if (!result.success && result.error) {
        markdown += `  - Error: ${result.error}\n`;
      }
    }
  }
  markdown += `\n`;
} else {
  markdown += `## üí¨ Claude's Analysis\n\n`;
  markdown += data.claude_response + `\n\n`;
}

// Token Usage
markdown += `## üìä Token Usage\n\n`;
markdown += `- **Input tokens:** ${data.token_usage.input_tokens.toLocaleString()}\n`;
markdown += `- **Output tokens:** ${data.token_usage.output_tokens.toLocaleString()}\n`;
markdown += `- **Total tokens:** ${data.token_usage.total_tokens.toLocaleString()}\n\n`;
markdown += `**Cost:** $${data.cost_usd.toFixed(4)} USD (‚Ç¨${data.cost_eur.toFixed(4)} EUR)\n\n`;

// Financial Balance
markdown += `üí∞ **Financial Balance:** \n`;
markdown += `   - This task cost: $${data.cost_usd.toFixed(4)}\n`;
markdown += `   - Zostatok: ~$2.50 USD\n\n`;

markdown += `---\n\n`;
markdown += `*Generated by Claude Dev Automation (n8n)*\n`;

return {
  json: {
    response_markdown: markdown
  }
};
